export interface etasIniButtons {
    Button: string,
    row: number
}
export class accButtonInIni{
    Button:string;
    data: string;
    constructor(button:string, data:string)
    {
        this.Button = button;
        this.data = data;
    }
}
export class TelActionIni{

    Idx:string;
    Caption: string;
    Function: string;
    Data: string;
    //
    constructor(idx:string, caption:string,functionX:string,data:string )
    {
        this.Idx        = idx;
        this.Caption    = caption;
        this.Function   = functionX;
        this.Data       = data;
        }
}
export class accbutton {
    public id: string;
    public title: string;
    public description: string;
    public class: string;
    public color: string;
    public click: any;
    public canDrag:boolean;
    public isMust:boolean;
    public img: string;
    public isSet:boolean;
    public code:string;
    public data:string;
    public titlesrc:string;
    public datatype: string;
    public isdisable?:boolean;
    public setClass?: string;

    constructor(idx, titlex, classx, colorx, clickxx, imgx,candrag, ismust,description,code,datatype) {
        this.id = idx;
        this.title = titlex;
        this.titlesrc = titlex;
        this.class = classx;
        this.color = colorx;
        this.click = clickxx;
        this.img = imgx;
        this.canDrag = candrag;
        this.isMust = ismust;
        this.isSet = false;
        this.description = description;
        this.code = code;
        this.data = "";
        this.datatype = datatype;
    }
}

