        http_response_prefixes[0] = HTTP_RESPONSE1_PREFIX;

        var HTTP_RESPONSE1_SUFFIX: string = this.RetrieveCRMActionParameterByName("HTTP_RESPONSE1_SUFFIX", crmaction, oc);
