        if (HTTP_RESPONSE3_SUFFIX == this.CPF_DOES_NOT_EXIST || HTTP_RESPONSE3_SUFFIX == this.CPF_NOT_FOUND_IN_CALL)
        {
            HTTP_RESPONSE3_SUFFIX = "";
