export class Key_Desc
{
    constructor(key:string, desc:string,id:string)
    {
        this.Key = key;
        this.Desc = desc;
        this.Id   = id;
        this.More = "";
        this.Flag = false;
    }

    public Key:string;
    public Desc:string;
    public Id:string;
    public More:string;
    public Flag:boolean;

}

