            a.group_name = GroupsList[idx].Desc;
            //a.totQACD = (Number(tt[2]) + 0).toString();// Number(tt[7])
            a.totQACD = (Number(tt[7]) + 0).toString();
            //var avg:number = Number(tt[8]);
            // if (Number(tt[3]) > 0)
            // {
