// -------------------------------------------------
const EmptyRsponse: OneCall = new OneCall("-1");

//=======================
export class Setup {
    public constructor(val: string, vv: string) {
        this.value = val;
        this.viewValue = vv;
    }

    value: string;
    viewValue: string;
}

export class SetupGroup {
    disabled?: boolean;
    name: string;
    setup: Setup[];

    public constructor(d: boolean, n: string) {
        this.disabled = d;
        this.name = n;
        this.setup = [];
    }
}

export class EventData {
    name: string;
    value: any;

    constructor(name: string, value: any) {
        this.name = name;
        this.value = value;
    }
}

@Component({
