                                b.class += " breakin";
                            break;
                            //
                            case "Whisper":
                                b.class += " whisper";
                            break;
                            //
                            case "RecordGreeting":
                                b.class += " recordgreeting";
                            break;
                            //
                           case "LoginGroup":
                                //b.class += " group";
                                var j = TT.AAC.ACC.m_GroupsList.map(e => e.Key).indexOf(b.data);
                                if(j == -1)
                                    button_done = false;
                                else
                                {
                                    if (b.data != '') {b.class += " group";}
                                    b.title = bt + "\n" + TT.AAC.ACC.m_GroupsList[j].Desc;
                                    b.id = b.id + '.' + b.data;
                                }
                            break;
                            //
                            default: break;
                        }
                    }
                } catch (e) {
                    this.AAC.log(" !!!! constructor, exception: " + e.message );
                }
                if(button_done)
                    TT.tiles.push(b);
