         if (i == -1) {return;}
         this.AAC.etasIni.CRM[i].URL = this.slectetPopup.URL;
     }

   // ===================[play5Seconds]====================
    play5Seconds(audio)
    {
        if (typeof audio.loop == 'boolean')
        {
            audio.loop = true;

        }
        else
        {
            audio.addEventListener('ended', function() {
                this.currentTime = 0;
                this.play();
            }, false);
        }
       audio.play();
        setTimeout(function()
        {
             audio.pause();
        }, 6000);

    }
