    this.AAC.NotificationInterval = 5000;
    this.AAC.timerSubscribe();

    if (this.AAC.agaenLogin.username != "")
    {
      var response: any;

      if (this.AAC.isGateLogin() && !this.AAC.isGateSignOut())
        response = this.AAC.accLogonJWT(this.AAC.agaenLogin);

