export class Setup {
    public constructor(val:string, vv:string){
        this.value = val;
        this.viewValue = vv;    
        this.selected =  false;
        this.data = "";

    }
    value: string;
    viewValue: string;
    selected:boolean;
    data:string;
  }
  export class SetupGroup {
    disabled?: boolean;
    name: string;
    setup: Setup[];
    public constructor(d:boolean, n:string){
        this.disabled = d;
        this.name = n;    
        this.setup = [];
    }
  }


