            //  window.URL.revokeObjectURL(this.src);
           //}
           li.appendChild(img);
           const info = document.createElement("span");
           info.innerHTML = files[i].name + ": " + files[i].size + " bytes";
           li.appendChild(info);
         }
       }
      }
