     animation-delay: 0s;
 }
.circle_bow:nth-child(2) {
    transform: translate3d(-1.625em, 0, -4.875em);
}
.circle_bow:nth-child(2) .squisher2 {
    animation-delay: 0.5s;
}
.circle_bow:nth-child(3) {
    transform: translate3d(1.625em, 0, -4.875em);
}
.circle_bow:nth-child(3) .squisher2 {
    animation-delay: 1s;
}

.squisher2 {
    transform-origin: 0 1.375em;
    transform: scaleY(0);
    animation: ani2 3.5s ease-in-out infinite;
}


@keyframes ani2 {
    100%, 100%, 10% {
        transform: scaleY(0);
    }
    25% {
        transform: scaleY(0.5);
    }
}

.intro {
    transform: rotateX(0deg) rotateY(0deg);
}
.intro, .intro * {
    position: absolute;
    top: 50%;
    left: 50%;
    transform-style: preserve-3d;
}

